import React, { useEffect, useState } from 'react'
import '../assets/css/blog.css'
import ListaCategorias from '../componentes/ListaCategorias'
import ListaPosts from '../componentes/ListaPosts'
import { Route, useParams, useRouteMatch } from 'react-router-dom/cjs/react-router-dom.min'
import { busca } from '../api/api'

const Categoria = () => {
    const { id } = useParams ()
    const { path } = useRouteMatch()

    const [subcagorias, setSubcategorias] = useState([])

    useEffect(() => {
        busca(`/categorias/${id}`, (categoria) => {
            setSubcategorias(categoria.subcagorias)
        })
    }, [id])
    
    return(
        <>
        <div className="container">
            <h2 className="titulo-pagina">Pet Notícias</h2>
        </div>

        <ListaCategorias />

        <Route exact path={`${path}/`}>
            <ListaPosts url={`/posts?categoria=${id}`}/>
        </Route>
        
        </>
    )
}

export default Categoria
